export declare const customHighlightCSS = "\n  :not(pre) > code[class*=\"language-\"],\n  pre[class*=\"language-\"] {\n    color: white;\n    background: #111827; \n  }\n    \n  pre[class*=\"language-\"] > code[class*=\"language-\"] {\n    position: relative;\n  }\n\n  pre[class*=\"language-\"]{\n    padding: 0.5rem;\n  }\n\n  .line-numbers-rows > span:before {\n    content: counter(linenumber);\n    color: #9FFCEF; \n    display: block;\n    padding-right: 0.8em;\n    text-align: right;\n  }\n\n  .line-highlight {\n    background: rgba(71, 85, 105, 0.25);\n  }\n\n  .line-numbers .line-numbers-rows {\n    border-right: 1px solid #6B7280;\n  }\n\n  pre[class*=\"language-\"] {\n    padding: 1em;\n    margin: 0 0 0.5em 0; \n    overflow: auto;\n  }\n\n  pre[class*=\"language-\"] ::selection {\n    background: white; \n    color: black; \n  }\n";
